package Loops;

public class Floyds_format {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//	int num=Integer.parseInt(args[0]);
		int  num=3;
		for(int i=1;i<=num;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print("* ");
				
			}
			
		System.out.print("\n");
		}

	}

}
